<template>
	<view class="p-lr-30">
		<view class="mx-repeat mx-line m-t-24"
			style="background-image:url('data:image/png;base64,') ;">
			<view class="mx-flex mx-flex-between">
				<view class="white">
					<block v-if="info.guessing_status==-99"></block>
					<block v-if="info.guessing_status==0"></block>
					<block v-if="info.guessing_status==1"></block>
					<block v-if="info.guessing_status==-1"></block>
				</view>
				<view class="white">{{info.competition_name}}</view>
			</view>

			<view class="mx-line-center mx-flex mx-flex-model mx-flex-between">
				<view class="textcenter">
					<!-- <image src="../../static/logo.png" style="width: 78rpx;height: 78rpx;"></image> -->
					<image :src="team_a_logo_path" style="width: 78rpx;height: 78rpx;border-radius: 50%;"></image>
					<view class="f30 white">{{info.team_a_name}}</view>
				</view>
				<text class="p-lr-8 p-tb-4 white  m-b-20"
					:class="info.team_a_grade>info.team_b_grade?'bg858':'bg71'">{{info.team_a_grade==info.team_b_grade?'':info.team_a_grade>info.team_b_grade?'':''}}</text>
				<view class="bold f40 white p-lr-20">
					{{info.team_a_grade}} : {{info.team_b_grade}}
				</view>
				<text class="p-lr-8 p-tb-4 white  m-b-20"
					:class="info.team_a_grade<info.team_b_grade?'bg858':'bg71'">{{info.team_a_grade==info.team_b_grade?'':info.team_a_grade>info.team_b_grade?'':''}}</text>

				<view class="textcenter">
					<!-- <image src="../../static/logo.png" style="width: 78rpx;height: 78rpx;"></image> -->
					<image :src="team_b_logo_path" style="width: 78rpx;height: 78rpx;border-radius: 50%;"></image>
					<view class="f30 white">{{info.team_b_name}}</view>
				</view>
			</view>
			<image class="mx-margin-autolr img" src="@/static/schedule/1.png" style="width: 151rpx;height: 181rpx;">
			</image>
		</view>

			<view class="mx-flex mx-flex-between m-b-28 m-t-24">
				<view class="c9E1">{{info.team_a_name}}</view>
				<view class="c933">{{info.team_b_name}}</view>
			</view>
			<pk-bar :pkData="pkData"></pk-bar>
			<view class="mx-flex mx-flex-between m-b-28 m-t-24" >
				<button class="bg9E1 white radius40" size='mini' @click="zhichi(0)"
					:class="info.guessing_status!=-99 && item.team_a_guessing_cnt>0?'bgCDC c999':''">
