						<view class="mx-th mx-td" style="width: 220rpx;">
							<image :src="imgteam?imgteam:'/static/logo.png'" class="m-r-10"
								style="width: 50rpx;height: 50rpx;border-radius: 50%;"></image>
							<text class="one-text white" style="width: 150rpx;">{{item.nickname}}</text>
						</view>
