						<view class="mx-th mx-td">{{item.three_pointer}}</view>
						<view class="mx-th mx-td">{{item.four_pointer}}</view>
					</view>
					<view v-else></view>



				</view>
			</view>
		</view>
		<!-- <button @click="open"></button> -->
		<uni-popup ref="popup" type="center">
			<view class="center-pop">
				<input class="mx-input" v-model="gold_cnt" placeholder="" />
				<button class="m-t-40 mx-btn bg858 white f36" @click="post"></button>
			</view>
		</uni-popup>

	</view>
</template>

<script>
	export default {
		data() {
			return {
				playerinfo: {},
				current: 0,
				items: ['', ''],
				state: '',
				info: {},
				pkData: {
					left: 0,
					right: 0
				},
				playerlist: [],
				gold_cnt: '',
				type: '',
				bsID: '',
				team_a_logo_path: '',
				team_b_logo_path: '',
				imgteam: '',
			}
		},
		onLoad(option) {
			this.info = this.$storage.get('schedule')
			this.bsID = this.$storage.get('schedule').ID
			this.team_a_logo_path = this.$storage.get('schedule').team_a_logo_path
			this.team_b_logo_path = this.$storage.get('schedule').team_b_logo_path
			console.log(this.info)
			// this.open()
			console.log(this.info)
			this.items = [this.info.team_a_name, this.info.team_b_name]
			this.postguessingshow();
			this.postmatchshow();
		},
		methods: {
			post() {
				if (this.gold_cnt == '') {
					uni.showToast({
						title: '',
						icon: 'none'
					})
					return
				}
				console.log(this.info.ID)
				console.log(this.info)
				let competition_id = this.playerinfo.competition_id
				let match_id = this.bsID
				let win_team_id = this.type == 0 ? this.playerinfo.team_a_id : this.playerinfo.team_b_id
				// let gold_cnt = 1
				this.$api.guessingstore({
					win_team_id: win_team_id,
					competition_id: competition_id,
					match_id: match_id,
					gold_cnt: this.gold_cnt
				}).then(res => {
					console.log('', res)
					this.$refs.popup.close()
					uni.showToast({
						title: res.msg,
						icon: 'none'
					})
					// this.playerinfo = res.data;
					this.postguessingshow();
				})
			},
			zhichi(type) {
				this.type = type
				console.log(type, this.playerinfo)
				this.open()

			},
			open() {
				this.$refs.popup.open()
			},
			onClickItem(e) {
				this.current = e.currentIndex
				console.log(e)
				this.playerrefresh();
			},
			postmatchshow() {
				this.$api.matchshow({
					match_id: this.bsID
				}).then(res => {
					console.log('', res)
					this.playerinfo = res.data;
					this.playerrefresh();
				})
			},
			playerrefresh() {
				this.playerlist = this.current == 0 ? this.playerinfo.team_a.member_list : this.playerinfo.team_b
					.member_list;
				this.imgteam = this.current == 0 ? this.playerinfo.team_a.logo_path : this.playerinfo.team_b.logo_path
			},
			postguessingshow() {
				this.$api.guessingshow({
					match_id: this.bsID
				}).then(res => {
					console.log('', res)

					this.info = res.data;
					this.pkData.left = res.data.team_a_guessing_cnt
					this.pkData.right = res.data.team_b_guessing_cnt
				})
			}
		}
	}
</script>

